//Code provide by Mikael Ainalem
//Maaf masih belajar tentang animasi grafis SVG karena waktu kepepet,
//jadinya copas
//Using Anime.JS The Open-Source JavaScript Animation Engine by Julia Garnier
//https://github.com/juliangarnier/animejs
var current = null;
document.querySelector('#email').addEventListener('focus', function(e) {
  if (current) current.pause();
  current = anime({
    targets: 'path',
    strokeDashoffset: {
      value: 0,
      duration: 700,
      easing: 'easeOutQuart'
    },
    strokeDasharray: {
      value: '240 1386',
      duration: 700,
      easing: 'easeOutQuart'
    }
  });
});
